	public void test4(){
		Pattern tagNamePattern = Pattern.compile("(?is)<[/?]?([a-z]+)\\s?.*?>");
		String _html = "<p class='aa'>paragraph</p >&nbsp;<img src='aa' /> <formulas> ";
			
		Set<String> whiteList = new TreeSet<String>();
		whiteList.add("img");
		whiteList.add("table");
			//
			// whiteList를 보고 tag를 skip한다.
			//
			//
			//1.시작태그
			//
			Matcher matcher = tagNamePattern.matcher(_html);
			StringBuffer result = new StringBuffer();
			while(matcher.find()){
				String tagName = matcher.group(1);
				if(tagName != null)
					tagName = tagName.toLowerCase();
				if(!whiteList.contains(tagName)){
					//삭제한다
					matcher.appendReplacement(result, " ");
				}
			}
			matcher.appendTail(result);
			_html = result.toString();
			
			System.out.println(_html);
	}
	
